	public ActionForward save(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws IOException {
		response.setContentType("text/html;charset=utf-8");
		request.setCharacterEncoding("utf-8");
		ConfigMistypeForm configMistypeForm = (ConfigMistypeForm) form;
		String operation = request.getParameter("operation");
		String rname = configMistypeForm.getRname();
		String prid = configMistypeForm.getPrid();
		String sql = "";
		String[] params = new String[0];
		String rid = "";
		DBTools dbTool = new DBTools();	
		boolean result = false;
		
		if (prid != null && !dbTool.queryISEXIST("select * from SYS_JBREASON where RID=?", new String[]{prid})) {

			sql = "select RID from SYS_JBREASON where PRID=? order by RID desc limit 1";
			String maxRid = dbTool.queryMistypeRid(sql, new String[] { prid });

			if (!maxRid.equals("")) 
				rid = String.valueOf(Integer.parseInt(maxRid) + 1);
			else rid = String.valueOf(Integer.parseInt(prid) + 1);
			if (operation.equals("new")) {
				sql = "insert into SYS_JBREASON(RID,RNAME,PRID,RSORT,ISJC) values(?, ?, ?, ?, ?)";
				params = new String[] { rid, rname, prid, "1", "0" };
			} else if (operation.equals("edit")) {
				rid = configMistypeForm.getRid();
				sql = "update SYS_JBREASON set RNAME=?, PRID=? where rid=?";
				params = new String[] { rname, prid, rid };
			}
			result = dbTool.insertItem(sql, params);
		} else  if (prid == null){

			sql = "select RID from SYS_JBREASON where ISJC=1 order by RID desc limit 1";
			String maxRid = dbTool.queryMistypeRid(sql, new String[0]);

			if (!maxRid.equals("")) {
				rid = String.valueOf(Integer.parseInt(maxRid) + 1000);
				if (operation.equals("new")) {
					sql = "insert into SYS_JBREASON(RID,RNAME,PRID,RSORT,ISJC) values(?, ?, ?, ?, ?)";
					params = new String[] { rid, rname, "0", "1", "1"};
				} else if (operation.equals("edit")) {
					rid = configMistypeForm.getRid();
					sql = "update SYS_JBREASON set RNAME=? where rid=?";
					params = new String[] { rname, rid };
				}
				result = dbTool.insertItem(sql, params);
			}
		}
		PrintWriter out = response.getWriter();
		JSONObject json = new JSONObject();
		if(result)
		{
			request.getSession().setAttribute("configMistypeFlag", "true");
			json.put("statusCode", 200);
			json.put("message", "保存成功！");
			json.put("callbackType", "closeCurrent");
		}
		else
		{
			json.put("statusCode", 300);
			json.put("message", "保存失败！");
		}
		out.write(json.toString());
		out.flush();
		return null;
	}
	public ActionForward edit(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws IOException {
		response.setContentType("text/html;charset=utf-8");
		request.setCharacterEncoding("utf-8");	
		
		ConfigMistypeForm configMistypeForm = (ConfigMistypeForm) form;
		String id = request.getParameter("id");
		DBTools dbTools = new DBTools();
		String sql = "select a.*,b.RNAME as PNAME from SYS_JBREASON a left join SYS_JBREASON b on a.PRID=b.RID where a.ID=?";
		MistypeBean mistypeBean = dbTools.queryMistypeBean(sql, new String[]{id});
		ArrayList result = new ArrayList();
		if(mistypeBean!=null)
		{
			result.add(mistypeBean);
			configMistypeForm.setRecordNotFind("false");
			configMistypeForm.setRecordList(result);
			return mapping.findForward("edit");
		}
		else
		{
			configMistypeForm.setRecordNotFind("true");
			return mapping.findForward("initError");
		}
	}
	
	/**
	 * weight init
	 * 
	 * 
	 * 
	 */
	public ActionForward weight(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception {
		
		response.setContentType("text/html;charset=utf-8");
		request.setCharacterEncoding("utf-8");
		
		String type = request.getParameter("type");
		String sql = "";
		String[] params = new String[0];
		
		if(type.equals("0")){
			sql = "select * from SYS_JBREASON where RSORT=1 and ISJC=1";
		} else {
			sql = "select * from SYS_JBREASON where RSORT=1 and PRID=?";
			params = new String[]{type};
		}
		
				
		DBTools db = new DBTools();
		ResultSet rs = db.queryRsList(sql, params);
		
		List<String> lstTree = db.queryMistype(rs);
	        
		
		request.setAttribute("weightJson", JSONArray.fromObject(lstTree).toString());
		
		return mapping.findForward("weight");
	}
	/**
	 * weight save
	 * 
	 * 
	 * 
	 */
	public ActionForward saveWeight(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception {
		
		response.setContentType("text/html;charset=utf-8");
		request.setCharacterEncoding("utf-8");
		
		String weightList = request.getParameter("weightList");
		String sql = "";
		Boolean result = false;
		
		
		DBTools db = new DBTools();
		

		org.json.JSONArray weights = new org.json.JSONArray(weightList);	
			
		for (int i = 0; i < weights.length(); i++)
		{
			  String rid = weights.getJSONObject(i).getString("rid");
			  String weight = weights.getJSONObject(i).getString("weight");
			  sql = "update SYS_JBREASON set WEIGHT = ? where RID= ?";
			  String[] params = new String[]{weight, rid};
			  result = db.insertItem(sql, params);
			  if(!result)
				  break;
		}

		
		PrintWriter out = response.getWriter();
		JSONObject json = new JSONObject();
		if(result)
		{			
			request.getSession().setAttribute("configMistypeFlag", "true");
			json.put("statusCode", 200);
			json.put("message", "更新成功！");
			json.put("callbackType", "closeCurrent");
		}
		else
		{
			json.put("statusCode", 300);
			json.put("message", "更新失败！");
		}
		out.write(json.toString());
		out.flush();
		out.close();
		
		return null;
	}
